ALTER TABLE personal MODIFY COLUMN rol ENUM("cajero", "planchero", "seguridad") NOT NULL;

-- 2. ASEGURAR PERSONAL
SELECT "Claudio", "seguridad", 0, 0
WHERE NOT EXISTS (SELECT 1 FROM personal WHERE nombre = "Claudio");

